import React, { useState } from 'react';

function QuizPersonalizado() {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [userResponses, setUserResponses] = useState([]);

  const questions = [
    {
      type: 'choice',
      question: "Onde foi nosso primeiro encontro?",
      options: ["CafÃ©", "Cinema", "Parque", "Restaurante"],
      answer: "CafÃ©"
    },
    {
      type: 'text',
      question: "Qual o momento mais engraÃ§ado que compartilhamos?",
      keyword: "praia"  // Exemplo de palavra-chave
    },
    // Adicione mais perguntas conforme necessÃ¡rio
  ];

  const handleChoiceAnswer = (option) => {
    if (option === questions[currentQuestionIndex].answer) {
      setScore(score + 1);
      alert('Correto! ðŸŒŸ');
    } else {
      alert('Ops, nÃ£o foi dessa vez! ðŸ˜…');
    }
    setCurrentQuestionIndex(currentQuestionIndex + 1);
  };

  const handleTextAnswer = (response) => {
    setUserResponses([...userResponses, response]);
    if (response.toLowerCase().includes(questions[currentQuestionIndex].keyword)) {
      alert('Resposta interessante! ðŸŒŸ');
    } else {
      alert('Obrigado pela resposta! ðŸ’¬');
    }
    setCurrentQuestionIndex(currentQuestionIndex + 1);
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 p-4">
      {currentQuestionIndex < questions.length ? (
        questions[currentQuestionIndex].type === 'choice' ? (
          <div className="w-full max-w-lg p-5 bg-white shadow-md rounded-lg">
            <p className="text-xl font-bold mb-4">{questions[currentQuestionIndex].question}</p>
            {questions[currentQuestionIndex].options.map((option, index) => (
              <button key={index}
                      onClick={() => handleChoiceAnswer(option)}
                      className="w-full mb-4 py-2 px-4 bg-blue-500 text-white rounded hover:bg-blue-700 transition duration-300">
                {option}
              </button>
            ))}
          </div>
        ) : (
          <div className="w-full max-w-lg p-5 bg-white shadow-md rounded-lg">
            <p className="text-xl font-bold mb-4">{questions[currentQuestionIndex].question}</p>
            <input type="text"
                   onBlur={(e) => handleTextAnswer(e.target.value)}
                   className="w-full p-2 border border-gray-300 rounded"
                   placeholder="Digite sua resposta aqui..." />
          </div>
        )
      ) : (
        <div className="text-center">
          <h2 className="text-3xl font-bold text-green-500">Quiz ConcluÃ­do!</h2>
          <p className="text-xl mt-2">Sua pontuaÃ§Ã£o foi: {score} de {questions.length}</p>
        </div>
      )}
    </div>
  );
}

export default QuizPersonalizado;
