import React, { useState } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import { useNavigate } from 'react-router-dom';
import 'react-toastify/dist/ReactToastify.css';

function QuizPersonalizado() {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [score, setScore] = useState(0); // Usado apenas para perguntas de m√∫ltipla escolha
  const [quizCompleted, setQuizCompleted] = useState(false);
  const [responses, setResponses] = useState([]); // Armazenar todas as respostas
  const navigate = useNavigate();

  const questions = [
    {
      type: 'choice',
      question: "Onde foi nosso primeiro encontro?",
      options: ["Caf√©", "Cinema", "Parque", "Restaurante"],
      answer: "Caf√©"
    },
    {
      type: 'text',
      question: "Qual foi o momento mais engra√ßado que compartilhamos?"
    },
    // Mais perguntas conforme necess√°rio
  ];

  const handleChoiceAnswer = (option) => {
    if (option === questions[currentQuestionIndex].answer) {
      setScore(score + 1);
      toast.success('Correto! üåü');
    } else {
      toast.error('Ops, n√£o foi dessa vez! üòÖ');
    }
    proceedToNextQuestion();
  };

  const handleTextResponse = (response) => {
    // Adiciona a resposta ao array de respostas
    setResponses([...responses, { question: questions[currentQuestionIndex].question, answer: response }]);
    toast.info('Resposta registrada! üí¨');
    proceedToNextQuestion();
  };

  const proceedToNextQuestion = () => {
    const nextQuestion = currentQuestionIndex + 1;
    if (nextQuestion < questions.length) {
      setCurrentQuestionIndex(nextQuestion);
    } else {
      setQuizCompleted(true);
    }
  };

  return (
    <div className="bg-quiz-pattern flex flex-col items-center justify-center min-h-screen p-4">
      <ToastContainer position="top-center" autoClose={5000} />
      {quizCompleted ? (
        <div className="bg-white/80 backdrop-blur-sm p-6 rounded-xl shadow-lg max-w-md w-full text-center">
          <h2 className="score-presentation text-2xl font-bold">Quiz conclu√≠do! Verifique as respostas.</h2>
          <button onClick={() => navigate('/resultados')} className="navigation-button bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
            Ver Respostas
          </button>
        </div>
      ) : (
        <div className="bg-white/80 backdrop-blur-sm p-6 rounded-xl shadow-lg max-w-md w-full">
          <h1 className="text-2xl font-bold">{questions[currentQuestionIndex].question}</h1>
          {questions[currentQuestionIndex].type === 'choice' ? (
            <div className="space-y-4">
              {questions[currentQuestionIndex].options.map((option, index) => (
                <button key={index} onClick={() => handleChoiceAnswer(option)} className="block w-full text-center py-3 px-4 bg-blue-500 text-white rounded hover:bg-blue-600">
                  {option}
                </button>
              ))}
            </div>
          ) : (
            <textarea
              className="w-full p-2 border border-gray-300 rounded mt-4"
              rows="4"
              placeholder="Digite sua resposta aqui..."
              onBlur={(e) => handleTextResponse(e.target.value)}
            />
          )}
        </div>
      )}
    </div>
  );
}

export default QuizPersonalizado;
