import { useState } from 'react';

function QuizPersonalizado() {
  const perguntas = [
    { pergunta: "Onde nos conhecemos?", respostas: ["Lugar A", "Lugar B", "Lugar C"], correta: "Lugar A" },
    // mais perguntas
  ];

  const [perguntaAtual, setPerguntaAtual] = useState(0);
  const [feedback, setFeedback] = useState("");

  const lidarComResposta = (resposta) => {
    if (resposta === perguntas[perguntaAtual].correta) {
      setFeedback("Isso mesmo! üòç");
    } else {
      setFeedback("Ops, tente novamente. üòÖ");
    }
  };

  return (
    <div className="p-4">
      <h2 className="text-2xl">{perguntas[perguntaAtual].pergunta}</h2>
      <div className="mt-4">
        {perguntas[perguntaAtual].respostas.map((resposta, index) => (
          <button key={index} className="block my-2 px-4 py-2 bg-blue-500 text-white rounded" onClick={() => lidarComResposta(resposta)}>
            {resposta}
          </button>
        ))}
      </div>
      <p className="mt-4">{feedback}</p>
    </div>
  );
}

export default QuizPersonalizado;
