import React, { useState } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function QuizPersonalizado() {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [userResponses, setUserResponses] = useState([]);
  const [currentTextResponse, setCurrentTextResponse] = useState("");

  const questions = [
    {
      type: 'choice',
      question: "Onde foi nosso primeiro encontro?",
      options: ["CafÃ©", "Cinema", "Parque", "Restaurante"],
      answer: "CafÃ©"
    },
    // Mais perguntas conforme necessÃ¡rio
  ];

  const handleChoiceAnswer = (option) => {
    if (option === questions[currentQuestionIndex].answer) {
      setScore(score + 1);
      toast.success('Correto! ðŸŒŸ');
    } else {
      toast.error('Ops, nÃ£o foi dessa vez! ðŸ˜…');
    }
    const nextQuestion = currentQuestionIndex + 1;
    setCurrentQuestionIndex(nextQuestion < questions.length ? nextQuestion : 0);
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen p-4" style={{ backgroundImage: `url('/')`, backgroundSize: 'cover' }}>
      <ToastContainer position="top-center" autoClose={5000} />
      <div className="bg-white/70 backdrop-blur-sm p-6 rounded-xl shadow-lg max-w-md w-full">
        <h1 className="text-xl font-semibold text-center mb-4">{questions[currentQuestionIndex].question}</h1>
        {questions[currentQuestionIndex].options.map((option, index) => (
          <button key={index}
                  onClick={() => handleChoiceAnswer(option)}
                  className="block w-full text-center py-2 px-4 bg-blue-500 text-white rounded-full my-2 hover:bg-blue-600 transition-colors">
            {option}
          </button>
        ))}
      </div>
    </div>
  );
}

export default QuizPersonalizado;
