import React, { useState } from 'react';

function QuizPersonalizado() {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [userResponses, setUserResponses] = useState([]);
  const [currentTextResponse, setCurrentTextResponse] = useState("");

  const questions = [
    {
      type: 'choice',
      question: "Onde foi nosso primeiro encontro?",
      options: ["CafÃ©", "Cinema", "Parque", "Restaurante"],
      answer: "CafÃ©"
    },
    {
      type: 'text',
      question: "Qual o momento mais engraÃ§ado que compartilhamos?",
      keyword: "praia"
    },
    // Mais perguntas conforme necessÃ¡rio
  ];

  const handleChoiceAnswer = (option) => {
    if (option === questions[currentQuestionIndex].answer) {
      setScore(score + 1);
      alert('Correto! ðŸŒŸ');
    } else {
      alert('Ops, nÃ£o foi dessa vez! ðŸ˜…');
    }
    proceedToNextQuestion();
  };

  const handleSubmitTextResponse = () => {
    setUserResponses([...userResponses, currentTextResponse]);
    if (currentTextResponse.toLowerCase().includes(questions[currentQuestionIndex].keyword)) {
      alert('Resposta interessante! ðŸŒŸ');
    } else {
      alert('Obrigado pela resposta! ðŸ’¬');
    }
    proceedToNextQuestion();
  };

  const proceedToNextQuestion = () => {
    const nextQuestion = currentQuestionIndex + 1;
    setCurrentTextResponse("");
    if (nextQuestion < questions.length) {
      setCurrentQuestionIndex(nextQuestion);
    } else {
      alert(`Quiz concluÃ­do! Sua pontuaÃ§Ã£o: ${score}`);
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 p-4">
      <div className="w-full max-w-2xl bg-white p-6 shadow rounded-lg">
        <h1 className="text-2xl font-bold">{questions[currentQuestionIndex].question}</h1>
        {questions[currentQuestionIndex].type === 'choice' ? (
          <div className="space-y-4 mt-4">
            {questions[currentQuestionIndex].options.map((option, index) => (
              <button key={index}
                      onClick={() => handleChoiceAnswer(option)}
                      className="block w-full text-left py-3 px-4 rounded-lg text-lg bg-blue-500 text-white hover:bg-blue-600">
                {option}
              </button>
            ))}
          </div>
        ) : (
          <div className="mt-4">
            <textarea
              value={currentTextResponse}
              onChange={(e) => setCurrentTextResponse(e.target.value)}
              className="w-full p-2 border border-gray-300 rounded"
              rows="4"
              placeholder="Digite sua resposta aqui...">
            </textarea>
            <button
              onClick={handleSubmitTextResponse}
              className="mt-4 py-2 px-4 bg-green-500 text-white rounded hover:bg-green-600">
              Enviar
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

export default QuizPersonalizado;
