import React, { useState, useEffect } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { useNavigate } from 'react-router-dom';

function QuizPersonalizado() {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [responses, setResponses] = useState([]);
  const [quizCompleted, setQuizCompleted] = useState(false);
  const navigate = useNavigate();

  const questions = [
    {
      type: 'choice',
      question: "Onde foi nosso primeiro encontro?",
      options: ["CafÃ©", "Cinema", "Parque", "Restaurante"],
      answer: "CafÃ©"
    },
    {
      type: 'text',
      question: "Qual foi o momento mais engraÃ§ado que compartilhamos?"
    },
    // Adicione mais perguntas conforme necessÃ¡rio
  ];

  const handleChoiceAnswer = (option) => {
    if (option === questions[currentQuestionIndex].answer) {
      setScore(prevScore => prevScore + 1);
      toast.success('Correto! ðŸŒŸ');
    } else {
      toast.error('Ops, nÃ£o foi dessa vez! ðŸ˜…');
    }
    proceedToNextQuestion();
  };

  const handleTextResponse = (response) => {
    setResponses(prevResponses => [...prevResponses, { question: questions[currentQuestionIndex].question, answer: response }]);
    proceedToNextQuestion();
  };

  const proceedToNextQuestion = () => {
    const nextQuestion = currentQuestionIndex + 1;
    if (nextQuestion < questions.length) {
      setCurrentQuestionIndex(nextQuestion);
    } else {
      setQuizCompleted(true);
    }
  };

  useEffect(() => {
    if (quizCompleted) {
      navigate('/historico', { state: { responses } });
    }
  }, [quizCompleted, navigate, responses]);

  return (
    <div className="bg-quiz-pattern flex flex-col items-center justify-center min-h-screen p-4">
      <ToastContainer position="top-center" autoClose={5000} />
      {!quizCompleted ? (
        <div className="bg-white/80 backdrop-blur-sm p-6 rounded-xl shadow-lg max-w-md w-full">
          <h1 className="text-2xl font-bold">{questions[currentQuestionIndex].question}</h1>
          {questions[currentQuestionIndex].type === 'choice' ? (
            <div className="space-y-4">
              {questions[currentQuestionIndex].options.map((option, index) => (
                <button key={index} onClick={() => handleChoiceAnswer(option)} className="block w-full text-center py-3 px-4 bg-blue-500 text-white rounded hover:bg-blue-600">
                  {option}
                </button>
              ))}
            </div>
          ) : (
            <textarea
              className="w-full p-2 border border-gray-300 rounded mt-4"
              rows="4"
              placeholder="Digite sua resposta aqui..."
              onBlur={(e) => handleTextResponse(e.target.value)}
            />
          )}
        </div>
      ) : null}
    </div>
  );
}

export default QuizPersonalizado;
