import React, { useState } from 'react';
import './Carta3D.css';

const Carta3D = () => {
  const [isOpen, setIsOpen] = useState(false);

  const handleOnClick = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className="carta-container">
      <div className={`carta ${isOpen ? 'open' : ''}`} onClick={handleOnClick}>
        <div className="carta-corpo">
          <div className="carta-frente"></div>
          <div className="carta-traseira"></div>
          <div className="carta-lacre"></div>
          <div className="carta-dobra-superior"></div>
          <div className="carta-dobra-inferior"></div>
        </div>
        {isOpen && (
          <div className="carta-conteudo">
            <div className="papel">
              <p>Te amo, Jennifer! Esta é uma carta especial para você.</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Carta3D;
