import React, { useState } from 'react';
import ReactAudioPlayer from 'react-audio-player';
import { Fireworks } from '@fireworks-js/react';
import './SurpresaFinal.css';

const SurpresaFinal = () => {
  const [response, setResponse] = useState(null);

  const handleResponse = (answer) => {
    setResponse(answer);
  };

  return (
    <div className={`surpresa-final-container ${response === 'yes' ? 'accepted' : ''}`}>
      <div className="content">
        <h1>Querida Jennifer,</h1>
        <p>
          Desde o momento em que nos conhecemos, minha vida mudou para melhor. Você trouxe
          alegria, amor e luz aos meus dias de uma forma que eu nunca imaginei ser possível.
          Cada momento ao seu lado é especial, e eu sou grato por cada um deles.
          Hoje, no nosso primeiro Dia dos Namorados juntos, quero fazer algo especial.
          Quero te pedir, do fundo do meu coração...
        </p>
        <h2>Uma música que me lembra você</h2>
        <ReactAudioPlayer
          src="/assets/music/your_song.mp3"
          controls
          className="audio-player"
        />
        <div className="request-section">
          <h2>Quer namorar comigo, Jennifer?</h2>
          <div className="buttons-container">
            <button className="response-button" onClick={() => handleResponse('yes')}>Sim</button>
            <button className="response-button" onClick={() => handleResponse('no')}>Não</button>
          </div>
        </div>
      </div>
      {response === 'yes' && (
        <>
          <Fireworks
            options={{ opacity: 0.5 }}
            style={{ top: 0, left: 0, width: '100%', height: '100%', position: 'fixed' }}
          />
          <ReactAudioPlayer
            src="https://p.scdn.co/mp3-preview/your_spotify_preview.mp3"
            autoPlay
            controls
            loop
            className="audio-player"
          />
        </>
      )}
      {response === 'no' && (
        <ReactAudioPlayer
          src="https://p.scdn.co/mp3-preview/your_spotify_preview.mp3"
          autoPlay
          controls
          loop
          className="audio-player"
        />
      )}
    </div>
  );
};

export default SurpresaFinal;
