import React from 'react';
import BarraNavegacao from './BarraNavegacao';

// Usando import direto se as imagens estão em 'src/assets/images'
import foto1 from '';
import foto2 from '../assets/images/WhatsApp Image 2024-06-11 at 04.38.38.jpeg';
import foto3 from '../assets/images/WhatsApp Image 2024-06-11 at 04.38.39.jpeg';
import foto4 from '../assets/images/WhatsApp Image 2024-06-11 at 04.38.40.jpeg';

function GaleriaMomentos() {
  const momentos = [
    { src: foto1, descricao: 'Nosso primeiro encontro. Um dia inesquecível.' },
    { src: foto2, descricao: 'Nosso passeio na praia. Sol e mar, perfeição.' },
    { src: foto3, descricao: 'Nosso jantar romântico. Apenas nós dois.' },
    { src: foto4, descricao: 'Nossa viagem dos sonhos. Aventuras e amor.' },
  ];

  return (
    <div>
      <BarraNavegacao />
      <div className="p-4 text-center">
        <h2 className="text-2xl font-bold mb-4">Nossos Momentos Registrados</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-2 lg:grid-cols-4 gap-4">
          {momentos.map((momento, index) => (
            <div key={index} className="relative group">
              <img src={momento.src} alt={`Momento ${index + 1}`} className="w-full h-full object-cover rounded-lg shadow-lg transition-transform duration-300 transform group-hover:scale-105" />
              <div className="absolute inset-0 bg-black bg-opacity-50 flex items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                <p className="text-white p-2">{momento.descricao}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default GaleriaMomentos;
