import React, { useState } from 'react';
import './Carta3D.css';

const Carta3D = () => {
  const [isOpen, setIsOpen] = useState(false);

  const handleOnClick = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className="carta-container">
      <div className={`carta ${isOpen ? 'open' : ''}`} onClick={handleOnClick}>
        <div className="carta-frente"></div>
        <div className="carta-traseira"></div>
        <div className="carta-lacre"></div>
        {isOpen && (
          <div className="carta-conteudo">
            <p>Te amo, Jennifer! Esta é uma carta especial para você.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Carta3D;
