import React, { useRef, useState } from 'react';
import { Canvas, useFrame, useThree } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';
import * as THREE from 'three';

function Carta({ position }) {
  const mesh = useRef();
  const [isOpen, setIsOpen] = useState(false);

  useFrame(() => {
    if (!isOpen) {
      mesh.current.rotation.y += 0.01;
    }
  });

  const handleOnClick = () => {
    setIsOpen(!isOpen);
  };

  return (
    <group position={position} onClick={handleOnClick}>
      {/* Parte frontal da carta */}
      <mesh ref={mesh} position={[0, 0, 0.05]}>
        <planeGeometry args={[3, 2]} />
        <meshStandardMaterial color={'#FFD700'} />
      </mesh>
      {/* Parte traseira da carta */}
      <mesh position={[0, 0, -0.05]}>
        <planeGeometry args={[3, 2]} />
        <meshStandardMaterial color={'#FFD700'} />
      </mesh>
      {/* Parte superior da carta */}
      <mesh rotation={[-Math.PI / 2, 0, 0]} position={[0, 1, 0]}>
        <planeGeometry args={[3, 0.1]} />
        <meshStandardMaterial color={'#FFD700'} />
      </mesh>
      {/* Parte inferior da carta */}
      <mesh rotation={[Math.PI / 2, 0, 0]} position={[0, -1, 0]}>
        <planeGeometry args={[3, 0.1]} />
        <meshStandardMaterial color={'#FFD700'} />
      </mesh>
      {/* Animação de abertura */}
      {isOpen && (
        <mesh position={[0, 0, -0.5]}>
          <planeGeometry args={[3, 2]} />
          <meshStandardMaterial color={'#FFFFFF'}>
            <TextContent />
          </meshStandardMaterial>
        </mesh>
      )}
    </group>
  );
}

const TextContent = () => {
  const { scene } = useThree();
  const fontLoader = new THREE.FontLoader();
  const font = fontLoader.parse(require('three/examples/fonts/helvetiker_regular.typeface.json'));

  const textOptions = {
    font,
    size: 0.2,
    height: 0.02,
  };

  return (
    <mesh>
      <textGeometry args={['Te amo, Jennifer! Esta é uma carta especial para você.', textOptions]} />
      <meshStandardMaterial color={'#000000'} />
    </mesh>
  );
};

const Carta3D = () => {
  return (
    <div className="canvas-container">
      <Canvas>
        <ambientLight intensity={0.5} />
        <pointLight position={[10, 10, 10]} />
        <Carta position={[0, 0, 0]} />
        <OrbitControls />
      </Canvas>
    </div>
  );
};

export default Carta3D;
