import React, { useRef, useEffect } from 'react';
import { Canvas, useFrame, useThree } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';
import * as THREE from 'three';

function Carta({ position }) {
  const mesh = useRef();
  useFrame(() => {
    mesh.current.rotation.y += 0.01;
  });

  const frontTexture = new THREE.TextureLoader().load('/path/to/front-image.jpg');
  const backTexture = new THREE.TextureLoader().load('/path/to/back-image.jpg');

  return (
    <mesh ref={mesh} position={position}>
      <boxGeometry args={[3, 2, 0.1]} />
      <meshStandardMaterial attachArray="material" map={frontTexture} />
      <meshStandardMaterial attachArray="material" map={backTexture} />
      <meshStandardMaterial attachArray="material" color={'#FFD700'} />
      <meshStandardMaterial attachArray="material" color={'#FFD700'} />
      <meshStandardMaterial attachArray="material" color={'#FFD700'} />
      <meshStandardMaterial attachArray="material" color={'#FFD700'} />
    </mesh>
  );
}

const Background = () => {
  const { scene } = useThree();

  useEffect(() => {
    const loader = new THREE.TextureLoader();
    loader.load('../assets/images/', (texture) => {
      scene.background = texture;
    });
  }, [scene]);

  return null;
};

const Carta3D = () => {
  return (
    <div className="h-screen">
      <Canvas>
        <ambientLight intensity={0.5} />
        <pointLight position={[10, 10, 10]} />
        <Background />
        <Carta position={[0, 0, 0]} />
        <OrbitControls />
      </Canvas>
    </div>
  );
};

export default Carta3D;
